"use client";

import { useState } from "react";
import { useLanguage } from "../contexts/language-context";
import AnimatedSection from "./animated-section";

export default function TruckTypes() {
	const { language } = useLanguage();
	const [activeCategory, setActiveCategory] = useState("Traila");

	const truckCategories = [
		{ id: "Traila", name: "تريلا", nameEn: "Traila" },
		{ id: "six", name: "سكس", nameEn: "Six" },
		{ id: "Lorry", name: "لوري", nameEn: "Lorry" },
		{ id: "Diana", name: "دينا", nameEn: "Diana" },
		{ id: "PickUp", name: "بيك اب", nameEn: "PickUp" },
	];

	const truckTypes = {
		Traila: [
			{
				name: "تريلا ثلاجة مبرد (20 طن)",
				nameEn: "Refrigerated Trailer (20 tons)",
				size: "الحجم يصل إلى 13.5 متر",
				sizeEn: "Size up to 13.5 meters",
				image: "/images/truckTypes/Trailla/refrigerator.png",
			},
			{
				name: "تريلا جوانب (20 طن)",
				nameEn: "Side Trailer (20 tons)",
				size: "الحجم يصل إلى 13.5 متر",
				sizeEn: "Size up to 13.5 meters",
				image: "/images/truckTypes/Trailla/sides.png",
			},
			{
				name: "تريلا جوانب ألماني (25 طن)",
				nameEn: "German Side Trailer (25 tons)",
				size: "الحجم يصل إلى 13.5 متر",
				sizeEn: "Size up to 13.5 meters",
				image: "/images/truckTypes/Trailla/shortSides.png",
			},
			{
				name: "تريلا ستارة (25 طن)",
				nameEn: "Curtain Trailer (25 tons)",
				size: "الحجم يصل إلى 13.5 متر",
				sizeEn: "Size up to 13.5 meters",
				image: "/images/truckTypes/Trailla/curtains.png",
			},
			{
				name: "تريلا سطحة (25 طن)",
				nameEn: "Flatbed Trailer (25 tons)",
				size: "الحجم يصل إلى 13.5 متر",
				sizeEn: "Size up to 13.5 meters",
				image: "/images/truckTypes/Trailla/flatbed.png",
			},
			{
				name: "تريلا ثلاجة مجمد (20 طن)",
				nameEn: "Frozen Trailer (20 tons)",
				size: "الحجم يصل إلى 13.5 متر",
				sizeEn: "Size up to 13.5 meters",
				image: "/images/truckTypes/Trailla/freezer.png",
			},
		],
		Diana: [
			{
				name: "دينا مبرد (4 طن)",
				nameEn: "Refrigerated Dyna (4 tons)",
				size: "الحجم يصل إلى 4.5 متر",
				sizeEn: "Size up to 4.5 meters",
				image: "/placeholder.jpg",
			},
			{
				name: "دينا بوش (3.5 طن)",
				nameEn: "Box Dyna (3.5 tons)",
				size: "الحجم يصل إلى 4.5 متر",
				sizeEn: "Size up to 4.5 meters",
				image: "/placeholder.jpg",
			},
			{
				name: "دينا مجمد (4 طن)",
				nameEn: "Frozen Dyna (4 tons)",
				size: "الحجم يصل إلى 4.5 متر",
				sizeEn: "Size up to 4.5 meters",
				image: "/placeholder.jpg",
			},
			{
				name: "دينا صندوق مغلق (4 طن)",
				nameEn: "Closed Box Dyna (4 tons)",
				size: "الحجم يصل إلى 4.5 متر",
				sizeEn: "Size up to 4.5 meters",
				image: "/placeholder.jpg",
			},
			{
				name: "دينا جوانب (4 طن)",
				nameEn: "Side Dyna (4 tons)",
				size: "الحجم يصل إلى 4.5 متر",
				sizeEn: "Size up to 4.5 meters",
				image: "/placeholder.jpg",
			},
		],
		// Add other categories with placeholder data
		six: [
			{
				name: "سكس مبرد (6 طن)",
				nameEn: "Refrigerated Six (6 tons)",
				size: "الحجم يصل إلى 6 متر",
				sizeEn: "Size up to 6 meters",
				image: "/placeholder.jpg",
			},
		],
		Lorry: [
			{
				name: "لوري مبرد (10 طن)",
				nameEn: "Refrigerated Lorry (10 tons)",
				size: "الحجم يصل إلى 8 متر",
				sizeEn: "Size up to 8 meters",
				image: "/placeholder.jpg",
			},
		],
		PickUp: [
			{
				name: "بيك اب (1 طن)",
				nameEn: "PickUp (1 ton)",
				size: "الحجم يصل إلى 2.5 متر",
				sizeEn: "Size up to 2.5 meters",
				image: "/placeholder.jpg",
			},
		],
	};

	const currentTrucks =
		truckTypes[activeCategory as keyof typeof truckTypes] || [];

	return (
		<section className='py-16 md:py-24 bg-gray-50'>
			<div className='container mx-auto px-4'>
				<AnimatedSection animation='fade-in'>
					<div className='max-w-4xl mx-auto text-center mb-12'>
						<p className='text-lg md:text-xl text-gray-700 leading-relaxed text-balance'>
							{language === "ar"
								? "لحرصنا على خدمة عملائنا في جميع القطاعات وفرنا العديد من أحجام شاحنات نقل البضائع بداية من الشاحنات الصغيرة مثل البيك أب والمتوسطة مثل الدينا وصولا للشاحنات الكبيرة كالتريلا واللوري بأنواعها بالإضافة إلى ذلك حرصنا على توفير جميع أنواع الشاحنات مثل الثلاجات والمغلقة الجاف أو جوانب وغيرها من الأنواع وحسب ما يتناسب مع منتجات كل عميل."
								: "We are committed to serving our customers in all sectors by providing various sizes of freight trucks, from small trucks like pickups and medium ones like Dyna to large trucks like trailers and lorries of all types. In addition, we ensure the availability of all types of trucks such as refrigerated, closed dry, or side trucks and others according to what suits each customer's products."}
						</p>
					</div>
				</AnimatedSection>

				{/* Truck Categories Navigation */}
				<div className='flex justify-center mb-8'>
					<div className='flex flex-wrap gap-2 bg-white rounded-xl p-2 shadow-lg'>
						{truckCategories.map((category) => (
							<button
								key={category.id}
								onClick={() => setActiveCategory(category.id)}
								className={`px-6 py-3 rounded-lg font-medium transition-all duration-300 ${
									activeCategory === category.id
										? "bg-[#3BA776] text-white shadow-md"
										: "text-gray-600 hover:bg-gray-100"
								}`}>
								{language === "ar" ? category.name : category.nameEn}
							</button>
						))}
					</div>
				</div>

				{/* Truck Types Grid */}
				<div className='grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6'>
					{currentTrucks.map((truck, index) => (
						<AnimatedSection
							key={index}
							animation='slide-up'
							delay={index * 100}>
							<div className='bg-white rounded-xl p-6 shadow-md hover:shadow-2xl transition-all duration-500 hover:-translate-y-3 hover:scale-105 cursor-pointer group'>
								<div className='flex justify-center mb-4'>
									<div className='w-32 h-32 bg-gray-100 rounded-lg flex items-center justify-center overflow-hidden'>
										<img
											src={truck.image}
											alt={language === "ar" ? truck.name : truck.nameEn}
											className='w-full h-full object-cover transition-transform duration-300 group-hover:scale-110'
											onError={(e) => {
												const target = e.target as HTMLImageElement;
												target.src = "/placeholder.jpg";
											}}
										/>
									</div>
								</div>
								<h3 className='text-lg font-bold text-gray-800 text-center mb-2 group-hover:text-[#3BA776] transition-colors'>
									{language === "ar" ? truck.name : truck.nameEn}
								</h3>
								<p className='text-sm text-gray-600 text-center'>
									{language === "ar" ? truck.size : truck.sizeEn}
								</p>
							</div>
						</AnimatedSection>
					))}
				</div>
			</div>
		</section>
	);
}
