"use client"

import { useState } from "react"
import { Button } from "./ui/button"
import { useLanguage } from "../contexts/language-context"

interface MapboxInputProps {
  placeholder: string
  value: string
  onChange: (value: string, coordinates?: [number, number]) => void
}

export default function MapboxInput({ placeholder, value, onChange }: MapboxInputProps) {
  const [inputValue, setInputValue] = useState(value)
  const { language } = useLanguage()

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newValue = e.target.value
    setInputValue(newValue)
    onChange(newValue)
  }

  const handleUseCurrentLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords
          const locationString = `${latitude.toFixed(6)}, ${longitude.toFixed(6)}`
          setInputValue(locationString)
          onChange(locationString, [longitude, latitude])
        },
        (error) => {
          console.error('Error getting location:', error)
          alert(language === 'ar' ? 'لا يمكن الوصول إلى الموقع' : 'Cannot access location')
        }
      )
    } else {
      alert(language === 'ar' ? 'الموقع غير مدعوم في هذا المتصفح' : 'Geolocation is not supported')
    }
  }

  return (
    <div className="flex flex-col sm:flex-row gap-2 w-full">
      <input
        type="text"
        placeholder={placeholder}
        value={inputValue}
        onChange={handleInputChange}
        className="flex-1 px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
      />
      <Button
        type="button"
        onClick={handleUseCurrentLocation}
        variant="outline"
        className="px-4 py-3 whitespace-nowrap"
      >
        {language === 'ar' ? 'الموقع الحالي' : 'Current Location'}
      </Button>
    </div>
  )
}
