"use client";

import { Truck, Snowflake, Box, Package } from "lucide-react";
import { useLanguage } from "../contexts/language-context";
import AnimatedSection from "./animated-section";

export default function TruckTypes() {
	const { t } = useLanguage();

	const truckTypes = [
		{
			name: t("refrigeratedTruck"),
			size: t("sizeUpTo"),
			icon: "refrigerated",
		},
		{
			name: t("boxTruck"),
			size: t("sizeUpTo"),
			icon: "box",
		},
		{
			name: t("frozenTruck"),
			size: t("sizeUpTo"),
			icon: "frozen",
		},
		{
			name: t("closedTruck"),
			size: t("sizeUpTo"),
			icon: "closed",
		},
		{
			name: t("sidesTruck"),
			size: t("sizeUpTo"),
			icon: "sides",
		},
	];

	const getIcon = (iconType: string) => {
		switch (iconType) {
			case "refrigerated":
				return <Snowflake className='w-10 h-10 text-emerald-600' />;
			case "frozen":
				return <Snowflake className='w-10 h-10 text-blue-600' />;
			case "box":
				return <Box className='w-10 h-10 text-gray-600' />;
			case "closed":
				return <Package className='w-10 h-10 text-gray-600' />;
			default:
				return <Truck className='w-10 h-10 text-gray-600' />;
		}
	};

	return (
		<section className='py-16 md:py-24 bg-gray-50'>
			<div className='container mx-auto px-4'>
				<AnimatedSection animation='fade-in'>
					<div className='max-w-4xl mx-auto text-center mb-12'>
						<p className='text-lg md:text-xl text-gray-700 leading-relaxed text-balance'>
							{t("truckTypesIntro")}
						</p>
					</div>
				</AnimatedSection>

				<div className='grid grid-cols-1 md:grid-cols-3 lg:grid-cols-5 gap-6'>
					{truckTypes.map((truck, index) => (
						<AnimatedSection
							key={index}
							animation='slide-up'
							delay={index * 100}>
							<div className='bg-white rounded-xl p-6 shadow-md hover:shadow-2xl transition-all duration-500 hover:-translate-y-3 hover:scale-105 cursor-pointer group'>
								<div className='flex justify-center mb-4 transition-transform duration-300 group-hover:scale-110 group-hover:rotate-6'>
									<div className='w-20 h-20 bg-gray-100 rounded-full flex items-center justify-center group-hover:bg-emerald-50 transition-colors duration-300'>
										{getIcon(truck.icon)}
									</div>
								</div>
								<h3 className='text-lg font-bold text-gray-800 text-center mb-2 group-hover:text-emerald-600 transition-colors'>
									{truck.name}
								</h3>
								<p className='text-sm text-gray-600 text-center'>
									{truck.size}
								</p>
							</div>
						</AnimatedSection>
					))}
				</div>
			</div>
		</section>
	);
}
