"use client"

import { useState } from "react"
import { useLanguage } from "../contexts/language-context"
import MapboxInput from "./mapbox-input-simple"

export default function Hero() {
  const { language, t } = useLanguage()
  const [deliveryType, setDeliveryType] = useState<"single" | "multiple">("single")
  const [pickupLocation, setPickupLocation] = useState("")
  const [deliveryLocation1, setDeliveryLocation1] = useState("")
  const [deliveryLocation2, setDeliveryLocation2] = useState("")

  return (
    <div className="relative min-h-[700px] bg-[#3BA776] overflow-hidden">
      <div className="absolute inset-0 opacity-10">
        <div
          className="absolute inset-0"
          style={{
            backgroundImage: `repeating-linear-gradient(
              45deg,
              transparent,
              transparent 35px,
              rgba(0,0,0,0.2) 35px,
              rgba(0,0,0,0.2) 70px
            )`,
          }}
        />
      </div>

      <div
        className="absolute left-0 right-0 h-[300px] bg-[#F5F5F5]"
        style={{
          bottom: "-50px",
          transform: "skewY(-3deg)",
          transformOrigin: "top left",
        }}
      />

      {/* Hero Content */}
      <div className="relative z-10 container mx-auto px-4 pt-8 pb-32">
        <h1 className="text-2xl md:text-3xl lg:text-4xl font-normal text-white text-center mb-8 leading-relaxed">
          {language === "ar"
            ? '"انقل بضاعتك وحمولتك أينما ترغب بطريقه سهلة وآمنة ."'
            : '"Transport your goods and cargo wherever you want in an easy and safe way."'}
        </h1>

        <div className="max-w-5xl mx-auto bg-white rounded-2xl shadow-lg p-8 md:p-10">
          <h2
            className={`text-xl md:text-2xl font-semibold text-[#3BA776] mb-6 ${language === "ar" ? "text-right" : "text-left"}`}
          >
            {language === "ar" ? "أطلب الآن" : "Order Now"}
          </h2>

          <div className={`flex ${language === "ar" ? "justify-end" : "justify-start"} gap-8 mb-6`}>
            <label className="flex items-center gap-2 cursor-pointer">
              <input
                type="radio"
                name="deliveryType"
                checked={deliveryType === "single"}
                onChange={() => setDeliveryType("single")}
                className="w-4 h-4 text-[#3BA776] accent-[#3BA776]"
              />
              <span className="text-gray-800 text-base">
                {language === "ar" ? "موقع توصيل واحد" : "Single Delivery Location"}
              </span>
            </label>
            <label className="flex items-center gap-2 cursor-pointer">
              <input
                type="radio"
                name="deliveryType"
                checked={deliveryType === "multiple"}
                onChange={() => setDeliveryType("multiple")}
                className="w-4 h-4 text-[#3BA776] accent-[#3BA776]"
              />
              <span className="text-gray-800 text-base">
                {language === "ar" ? "موقعين توصيل" : "Two Delivery Locations"}
              </span>
            </label>
          </div>

          <div className="flex flex-col md:flex-row gap-4 items-center">
            {language === "ar" ? (
              <>
                {/* Pickup Location - first in RTL */}
                <div className="flex-1 w-full">
                  <MapboxInput
                    placeholder="موقع التحميل"
                    value={pickupLocation}
                    onChange={(value) => setPickupLocation(value)}
                  />
                </div>

                {/* Arrow */}
                <div className="hidden md:block text-2xl text-gray-400 px-2">→</div>

                {/* Delivery Location 1 */}
                <div className="flex-1 w-full">
                  <MapboxInput
                    placeholder="عنوان التسليم 1"
                    value={deliveryLocation1}
                    onChange={(value) => setDeliveryLocation1(value)}
                  />
                </div>

                {/* Second Delivery Location - conditional */}
                {deliveryType === "multiple" && (
                  <>
                    <div className="hidden md:block text-2xl text-gray-400 px-2">→</div>
                    <div className="flex-1 w-full">
                      <MapboxInput
                        placeholder="عنوان التسليم 2"
                        value={deliveryLocation2}
                        onChange={(value) => setDeliveryLocation2(value)}
                      />
                    </div>
                  </>
                )}
              </>
            ) : (
              <>
                {/* Pickup Location - first in LTR */}
                <div className="flex-1 w-full">
                  <MapboxInput
                    placeholder="Pickup Location"
                    value={pickupLocation}
                    onChange={(value) => setPickupLocation(value)}
                  />
                </div>

                {/* Arrow */}
                <div className="hidden md:block text-2xl text-gray-400 px-2">→</div>

                {/* Delivery Location 1 */}
                <div className="flex-1 w-full">
                  <MapboxInput
                    placeholder="Delivery Address 1"
                    value={deliveryLocation1}
                    onChange={(value) => setDeliveryLocation1(value)}
                  />
                </div>

                {/* Second Delivery Location - conditional */}
                {deliveryType === "multiple" && (
                  <>
                    <div className="hidden md:block text-2xl text-gray-400 px-2">→</div>
                    <div className="flex-1 w-full">
                      <MapboxInput
                        placeholder="Delivery Address 2"
                        value={deliveryLocation2}
                        onChange={(value) => setDeliveryLocation2(value)}
                      />
                    </div>
                  </>
                )}
              </>
            )}
          </div>
        </div>

        <div className="relative z-20 mt-20 text-center">
          <p className="text-gray-800 text-lg md:text-xl lg:text-2xl font-light mb-6 max-w-3xl mx-auto leading-relaxed">
            {language === "ar"
              ? "نسعى لتحقيق شراكة متميزة عبر تسخير كل الإمكانات لخدمة عملائنا."
              : "We strive to achieve a distinguished partnership by harnessing all capabilities to serve our customers."}
          </p>
          <div className="flex justify-center gap-3 flex-wrap">
            <a href="#" className="inline-block">
              <img src="/google-play-badge.png" alt="Get it on Google Play" className="h-12 md:h-14" />
            </a>
            <a href="#" className="inline-block">
              <img src="/app-store-badge.png" alt="Download on the App Store" className="h-12 md:h-14" />
            </a>
          </div>
        </div>
      </div>
    </div>
  )
}
